mapper.select:
SELECT AUTH_ID , SYS_CREAT_ID , SYS_AMDR_ID , SYS_SVC_ID , SYS_RECD_CRET_DT , SYS_RECD_CHG_DT , AUTH_NM , USE_YN FROM AUTH_BAS
AND AUTH_ID = #{authId}
AND AUTH_NM = #{authNm}
AND USE_YN = #{useYn}
ORDER BY AUTH_ID DESC

mapper.update:
	UPDATE AUTH_BAS
 	   SET SYS_AMDR_ID = 'SYSTEM'
         , SYS_RECD_CHG_DT = NOW()

         , AUTH_NM = #{authNm}


         , USE_YN = #{useYn}

	 WHERE AUTH_ID = #{authId}
